import { ref } from "vue";
import { useStore } from "../pinia/store.js";
export default {
  name: "NoteList",
  setup() {
    const notesStore = useStore();
    const dialog = ref(false);
    const newTravel = ref({
      title: "",
      id: null,
      event: []
    });
    const createTravel = () => {
      if (!newTravel.value.title) {
        return;
      }
      notesStore.addTravel({
        title: newTravel.value.title,
        id: notesStore.nextIdTravel
      });
      newTravel.value = {
        title: "",
        id: null
      };
    };
    return {
      dialog,
      newTravel,
      createTravel
    };
  }
};
